var parse=require("../");var test=require("tape");test("numeric short args",function(a){a.plan(2);a.deepEqual(parse(["-n123"]),{n:123,_:[]});a.deepEqual(parse(["-123","456"]),{1:true,2:true,3:456,_:[]})});test("short",function(a){a.deepEqual(parse(["-b"]),{b:true,_:[]},"short boolean");a.deepEqual(parse(["foo","bar","baz"]),{_:["foo","bar","baz"]},"bare");a.deepEqual(parse(["-cats"]),{c:true,a:true,t:true,s:true,_:[]},"group");a.deepEqual(parse(["-cats","meow"]),{c:true,a:true,t:true,s:"meow",_:[]},"short group next");a.deepEqual(parse(["-h","localhost"]),{h:"localhost",_:[]},"short capture");a.deepEqual(parse(["-h","localhost","-p","555"]),{h:"localhost",p:555,_:[]},"short captures");a.end()});test("mixed short bool and capture",function(a){a.same(parse(["-h","localhost","-fp","555","script.js"]),{f:true,p:555,h:"localhost",_:["script.js"]});a.end()});test("short and long",function(a){a.deepEqual(parse(["-h","localhost","-fp","555","script.js"]),{f:true,p:555,h:"localhost",_:["script.js"]});a.end()});