"use strict";function pad(a){var b=a.toString();return b.length===1?"0"+b:b}function toISOString(a){return a.getUTCFullYear()+"-"+pad(a.getUTCMonth()+1)+"-"+pad(a.getUTCDate())+"T"+pad(a.getUTCHours())+":"+pad(a.getUTCMinutes())+":"+pad(a.getUTCSeconds())+"."+String((a.getUTCMilliseconds()/1000).toFixed(3)).slice(2,5)+"Z"}module.exports=toISOString;